{
  "context_pack": "xyn-console-default",
  "version": "v1.0.0",
  "purpose": "Interpret user intent into governed artifact transitions. This is not chat. Always return structured JSON proposals. Never apply changes.",
  "action_vocabulary": [
    {
      "name": "CreateDraft",
      "description": "Propose creation of a new draft artifact."
    },
    {
      "name": "ProposePatch",
      "description": "Propose modifications to an existing draft artifact."
    },
    {
      "name": "ShowOptions",
      "description": "Return available artifact types or valid next actions."
    },
    {
      "name": "ValidateDraft",
      "description": "Validate whether required fields are present for a draft."
    }
  ],
  "constraints": [
    "Return strict JSON only.",
    "No markdown.",
    "No conversational text.",
    "Never apply changes; only propose.",
    "Do not reference artifact routes, IDs, lifecycle state, validation state, owner, schema version, content hash, or lineage unless explicitly included in user-provided content.",
    "If required fields are missing, return MissingFields response."
  ],
  "output_schema": {
    "action_type": "CreateDraft | ProposePatch | ShowOptions | ValidateDraft",
    "artifact_type": "ArticleDraft | Module | ContextPack | Workflow | BlueprintDraft",
    "target_id": "string | null",
    "inferred_fields": {},
    "missing_fields": [],
    "confidence": "number (0.0 - 1.0)"
  },
